mapper.select:

select a.no, a.title, a.contents, a.hit, a.reg_date, a.g_no, a.o_no, a.depth, a.user_no ,b.name as user_name
from board a, user b
where a.user_no = b.no and a.title like #{kwd}
order by g_no DESC, o_no ASC limit #{curPage}, #{listPerPage}


update board
set hit = hit + 1
where no = #{no }
